const log4js = require('log4js');

const Decimal = require('@barchart/common-js/lang/Decimal');

const PositionCalculatorStrategy = require('./../PositionCalculatorStrategy');

module.exports = (() => {
	'use strict';

	const logger = log4js.getLogger('portfolio-common/data/calculators/position/timestamp/TimestampWriter');

	/**
	 * Generates the day for a snapshot.
	 *
	 * @public
	 * @extends {PositionCalculatorStrategy}
	 */
	class TimestampWriter extends PositionCalculatorStrategy {
		constructor() {
			super();
		}

		_calculate(transaction, currency) {
			return {
				date: transaction.date
			};
		}

		toString() {
			return '[TimestampWriter]';
		}
	}

	/**
	 * Indicates the day a snapshot was taken on (there could be multiple
	 * snapshots per day).
	 *
	 * @typedef TimestampSnapshot
	 * @type {Object}
	 * @property {Day} date - The day of the snapshot
	 */

	return TimestampWriter;
})();