const assert = require('@barchart/common-js/lang/assert'),
	is = require('@barchart/common-js/lang/is');

const DataOperation = require('./../DataOperation'),
	PortfolioDataProvider = require('./PortfolioDataProvider');

module.exports = (() => {
	'use strict';

	/**
	 * A {@link DataOperation} specific to the portfolio system.
	 *
	 * @interface
	 * @extends {DataOperation}
	 * @public
	 */
	class PortfolioDataOperation extends DataOperation {
		constructor() {
			super();
		}

		_validateDataProvider(dataProvider) {
			assert.argumentIsRequired(dataProvider, 'dataProvider', PortfolioDataProvider, 'PortfolioDataProvider');
		}

		toString() {
			return '[PortfolioDataOperation]';
		}
	}

	return PortfolioDataOperation;
})();
