const assert = require('@barchart/common-js/lang/assert');

const DataOperation = require('./../../../../DataOperation');

module.exports = (() => {
	'use strict';

	/**
	 * Queries portfolios.
	 *
	 * @public
	 * @extends {DataOperation}
	 */
	class Query extends DataOperation {
		constructor(user) {
			super();

			assert.argumentIsRequired(user, 'user', String);

			this._user = user;
		}

		_process(dataProvider) {
			return dataProvider.queryPortfoliosForLegacyUser(this._user);
		}

		toString() {
			return '[Query]';
		}
	}

	return Query;
})();
