const assert = require('@barchart/common-js/lang/assert');

const PortfolioDataOperation = require('./../../PortfolioDataOperation');

module.exports = (() => {
	'use strict';

	/**
	 * Queries positions.
	 *
	 * @public
	 * @extends {PortfolioDataOperation}
	 */
	class Query extends PortfolioDataOperation {
		constructor(user, portfolio, schema, limit, descending) {
			super();

			assert.argumentIsRequired(user, 'user', String);
			assert.argumentIsRequired(portfolio, 'portfolio', String);

			this._user = user;
			this._portfolio = portfolio;
			this._schema = schema;
			this._limit = limit;
			this._descending = descending;
		}

		_process(dataProvider) {
			return Promise.all([
				dataProvider.getPortfolio(this._user, this._portfolio),
				dataProvider.queryPositions(this._portfolio, this._schema, this._limit, this._descending)
			]).then((groups) => {
				if (groups[0]) {
					return groups[1];
				} else {
					return [ ];
				}
			});
		}

		toString() {
			return '[Query]';
		}
	}

	return Query;
})();
