const log4js = require('log4js');

const assert = require('@barchart/common-js/lang/assert');

const PortfolioDataOperation = require('./../../PortfolioDataOperation');

module.exports = (() => {
	'use strict';

	const logger = log4js.getLogger('portfolio-common/engine/portfolio/operations/positions/QueryPerUser');

	/**
	 * Queries all positions, regardless of portfolio, for a given user.
	 *
	 * @public
	 * @extends {PortfolioDataOperation}
	 */
	class QueryPerUser extends PortfolioDataOperation {
		constructor(user) {
			super();

			assert.argumentIsRequired(user, 'user', String);

			this._user = user;
		}

		_process(dataProvider) {
			return dataProvider.getPortfolios(this._user)
				.then((portfolios) => {
					return Promise.all(portfolios.map((portfolio) => dataProvider.getPositions(portfolio.user, portfolio.portfolio)));
				});
		}

		toString() {
			return '[QueryPerUser]';
		}
	}

	return QueryPerUser;
})();
