const assert = require('@barchart/common-js/lang/assert');

const DataOperationStage = require('./../../../DataOperationStage'),
	PortfolioDataOperation = require('./../../PortfolioDataOperation');

module.exports = (() => {
	'use strict';

	/**
	 * Flags a {@link Transaction} for writing to the persistence layer.
	 *
	 * @public
	 * @extends {PortfolioDataOperation}
	 * @param {String} user - The user identifier (of the portfolio's owner).
	 * @param {String} portfolio - The identifier of the portfolio.
	 * @param {String} position - The identifier of the position.
	 */
	class Update extends PortfolioDataOperation {
		constructor(user, portfolio, position) {
			super();

			assert.argumentIsRequired(user, 'user', String);
			assert.argumentIsRequired(portfolio, 'portfolio', String);
			assert.argumentIsRequired(position, 'position', String);

			this._user = user;
			this._portfolio = portfolio;
			this._position = position;
		}

		get stage() {
			return DataOperationStage.UPDATE;
		}

		_process(dataProvider) {
			return dataProvider.getPosition(this._user, this._portfolio, this._position)
				.then((position) => {
					return dataProvider.updatePosition(position);
				});
		}

		_equals(other) {
			return other instanceof Update && other._user === this._user && other._portfolio === this._portfolio && other._position === this._position;
		}

		toString() {
			return '[Update]';
		}
	}

	return Update;
})();
