const assert = require('@barchart/common-js/lang/assert'),
	Day = require('@barchart/common-js/lang/Day');

const PortfolioDataOperation = require('./../../PortfolioDataOperation');

module.exports = (() => {
	'use strict';

	/**
	 * Queries all transactions for a portfolio.
	 *
	 * @public
	 * @extends {PortfolioDataOperation}
	 */
	class QueryForPortfolio extends PortfolioDataOperation {
		constructor(user, portfolio, schema, start, end, limit, descending) {
			super();

			this._user = user;
			this._portfolio = portfolio;

			this._start = start;
			this._end = end;

			this._schema = schema;
			this._limit = limit;
			this._descending = descending;
		}

		_process(dataProvider) {
			return Promise.all([
				dataProvider.getPortfolio(this._user, this._portfolio),
				dataProvider.queryTransactionsForPortfolio(this._portfolio, this._schema, this._start, this._end, this._limit, this._descending)
			]).then((groups) => {
				if (groups[0]) {
					return groups[1];
				} else {
					return [ ];
				}
			});
		}

		toString() {
			return '[QueryForPortfolio]';
		}
	}

	return QueryForPortfolio;
})();
