const assert = require('@barchart/common-js/lang/assert'),
	Day = require('@barchart/common-js/lang/Day');

const PortfolioDataOperation = require('./../../PortfolioDataOperation');

module.exports = (() => {
	'use strict';

	/**
	 * Queries transactions.
	 *
	 * @public
	 * @extends {PortfolioDataOperation}
	 */
	class QueryForPosition extends PortfolioDataOperation {
		constructor(user, portfolio, position, schema, limit, descending) {
			super();

			this._user = user;
			this._portfolio = portfolio;
			this._position = position;
			this._schema = schema;
			this._limit = limit;
			this._descending = descending;
		}

		_process(dataProvider) {
			return Promise.all([
				dataProvider.getPortfolio(this._user, this._portfolio),
				dataProvider.queryTransactionsForPosition(this._portfolio, this._position, this._schema, this._limit, this._descending)
			]).then((groups) => {
				if (groups[0]) {
					return groups[1];
				} else {
					return [ ];
				}
			});
		}

		toString() {
			return '[QueryForPosition]';
		}
	}

	return QueryForPosition;
})();
