var DynamoProvider = require('@barchart/common-node-js/aws/DynamoProvider');

var PositionTable = require('./../../../../../database/tables/PositionTable');

class MockDynamoProvider extends DynamoProvider {
	constructor() {
		super({ region: 'ignored', prefix: 'mock', apiVersion: 'ignored' });
	}

	start() {
		return Promise.resolve(this._started = true);
	}
}

describe('When a PositionTable is created', function() {
	'use strict';

	var positions;

	beforeEach(function() {
		positions = new PositionTable(new MockDynamoProvider());
	});

	it('the table definition should be valid', function() {
		let valid;

		try {
			positions.definition.validate();

			valid = true;
		} catch (e) {
			valid = false;
		}

		expect(valid).toEqual(true);
	});
});